import React, { Component } from "react";

class Education extends Component {
  constructor (props) {
    super (props)
    this.state = {
      from :"",
      to: "",
      title:"",
      school: "",
      moreInfo: "",
    }
  }
  handleChangeFrom = (e) => {
    this.setState({
      from: e.target.value,
    });
  };

  handleChangeTo = (e) => {
    this.setState({
      to: e.target.value,
    });
  };

  handleChangeTitle = (e) => {
    this.setState({
      title: e.target.value,
    });
  };

  handleChangeSchool = (e) => {
    this.setState({
      school: e.target.value,
    });
  };

  handleChangeMoreInfo = (e) => {
    this.setState({
      moreInfo: e.target.value,
    });
  };
  onSubmitTask = (e) => {
    e.preventDefault();
    this.setState({
        from: "",
        to: "",
        school: ""
    })
  };

  render () {
    return (
    <form onSubmit={this.onSubmitTask}>
        <label>From</label>
        <input
          onchange={this.handleChangeFrom}
          type="date"
          id=""
         />
        <label>to</label>
        <input
          onchange={this.handleChangeTo}
          type="date"
          id=""
        />
        <label>Title</label>
        <input 
          onchange={this.handleChangeTitle}
          type="text" 
          id=""></input>
        <label>Name of School</label>
        <input 
          onchange={this.handleChangeSchool}
          type="text" 
          id=""></input>
        <label>More information about your Title</label>
        <input
          onchange={this.handleChangeMoreInfo}
          type="text"
          id=""></input>
        <button type="submit">Add Job Experience</button>
        <input type="text"></input>
      </form>
    )
  }
}

export default Education
