import React, { Component } from "react";

class Experience extends Component {
  constructor (props) {
    super (props);
    this.delete = this.delete.bind(this);
    this.state = {
      from : "",
      to: "",
      employer:"",
      position: "",
      moreInfo: "",
      new: {
        from:"",
        to:"",
        employer:"",
        position:"",
        moreInfo:""
      },
      experiences: []

    }
  }
  handleChangeFrom = (e) => {
    this.setState({
      from:e.target.value,

    });
  };

  handleChangeTo = (e) => {
    this.setState({
        to: e.target.value,
    });
  };

  handleChange = (e) => {
    this.setState({
        employer: e.target.value,
    });
  };

  handleChangeposition = (e) => {
    this.setState({
        position: e.target.value,
    });
  };

  handleChangeMoreInfo = (e) => {
    this.setState({
        moreInfo: e.target.value
    });
  };

  onSubmit = (e) => {
    e.preventDefault();
    this.setState ({
      new: {
        from: this.state.from,
        to: this.state.to,
        employer: this.state.employer,
        position: this.state.position,
        moreInfo: this.state.moreInfo
      },
    }, () => {

      console.log (this.state.new);
      this.setState ({
        experiences: this.state.experiences.concat(this.state.new),
      });
      console.log (this.state.experiences) 
    })

  }

  delete (e) {
    console.log (this.state.experiences)
    var array = [...this.state.experiences]; // make a separate copy of the array
    var index = e.target.id;
    console.log(index);
    if (index !== -1) {
      array.splice(index, 1);
      this.setState({
        experiences: array
      });
    }
  }

  render () {
    return (
    <form onSubmit={this.onSubmitTask}>
        <label>From</label>
        <input
         type="text"
         id=""
         />
        <label>to</label>
        <input
          type="text"
          id=""
        />
        <label>Employer</label>
        <input type="text" id=""></input>
        <label>Position</label>
        <input type="text" id=""></input>
        <label>More information about your role and position</label>
        <input type="text"></input>
        <button type="submit">Add Job Experience</button>
      </form>
    )
  }
}

export default Experience
